module.exports = function(api, options) {
    console.log('this is eason-build-test build', api)
    const { getWebpackConfig, setValue, emitHooks } = api
    const config = getWebpackConfig();
    // console.log('config', config)
    config
        .entry('index')
            .add('src/main.js')
            .end()
        .output
            .filename('[name].bundle.js')
            .path('dist')
            .end()
    setValue('name', {
        name: 'eason',
        value: '22',
        fn: function() {
            return 'test'
        }
    })
    emitHooks('plugin')
}